package sample_0321;

import java.util.*;

public class Selection {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		Scanner input = new Scanner(System.in);
		
		int con = input.nextInt();
		
		if(con % 2 == 0){
			System.out.println("JJJJ");
		}else {
			System.out.println("oooooo");
		}

	}

}
